package main

func ChooseNum(x, y int) int {
	// If x is greater than y, no valid range exists
	if x > y {
		return -1
	}

	// Start from the larger number and work backwards
	// to find the biggest even number
	for i := y; i >= x; i-- {
		if i%2 == 0 {
			return i
		}
	}

	// No even number found in the range
	return -1
}
