package main

func F(n int) []int {
	result := make([]int, n)

	for i := 1; i <= n; i++ {
		if i%2 == 0 {
			// i is even: calculate factorial
			fact := 1
			for j := 1; j <= i; j++ {
				fact *= j
			}
			result[i-1] = fact
		} else {
			// i is odd: calculate sum from 1 to i
			sum := 0
			for j := 1; j <= i; j++ {
				sum += j
			}
			result[i-1] = sum
		}
	}

	return result
}
