package main

import (
	"math"
	"sort"
)

func MoveOneBall(arr []int) bool {
	if len(arr) == 0 {
		return true
	}

	// Check all possible rotations
	for shift := 0; shift < len(arr); shift++ {
		if isSorted(arr, shift) {
			return true
		}
	}

	return false
}

func isSorted(arr []int, shift int) bool {
	// Check if array is sorted when rotated by 'shift' positions
	n := len(arr)
	for i := 0; i < n-1; i++ {
		current := arr[(i+shift)%n]
		next := arr[(i+1+shift)%n]
		if current > next {
			return false
		}
	}
	return true
}

