package main

func Tri(n int) []float64 {
	if n < 0 {
		return []float64{}
	}

	result := make([]float64, n+1)

	for i := 0; i <= n; i++ {
		result[i] = tri(i)
	}

	return result
}

func tri(n int) float64 {
	if n == 1 {
		return 3
	}

	if n%2 == 0 {
		return 1 + float64(n)/2
	}

	// n is odd
	return tri(n-1) + tri(n-2) + tri(n+1)
}
