package main

import (
	"strconv"
)

// Given a positive integer n, return the product of the odd Digits.
// Return 0 if all Digits are even.
// For example:
// Digits(1)  == 1
// Digits(4)  == 0
// Digits(235) == 15
func Digits(n int) int {
	s := strconv.Itoa(n)
	product := 1
	hasOdd := false

	for _, ch := range s {
		digit := int(ch - '0')
		if digit%2 == 1 {
			product *= digit
			hasOdd = true
		}
	}

	if !hasOdd {
		return 0
	}
	return product
}
