package main

import (
	"strings"
)

// Create a function that returns true if the last character
// of a given string is an alphabetical character and is not
// a part of a word, and false otherwise.
// Note: "word" is a group of characters separated by space.
//
// Examples:
// CheckIfLastCharIsALetter("apple pie") ➞ false
// CheckIfLastCharIsALetter("apple pi e") ➞ true
// CheckIfLastCharIsALetter("apple pi e ") ➞ false
// CheckIfLastCharIsALetter("") ➞ false
func CheckIfLastCharIsALetter(txt string) bool {
	if len(txt) == 0 {
		return false
	}

	lastChar := txt[len(txt)-1]

	// Check if last character is alphabetical
	if !((lastChar >= 'a' && lastChar <= 'z') || (lastChar >= 'A' && lastChar <= 'Z')) {
		return false
	}

	// Check if it's not part of a word (i.e., preceded by a space or it's the only character)
	if len(txt) == 1 {
		return true
	}

	// Check if the character before the last character is a space
	if txt[len(txt)-2] == ' ' {
		return true
	}

	return false
}

