package main

// CanArrange returns the largest index of an element which is not greater
// than or equal to the element immediately preceding it. If no such element
// exists then return -1.
func CanArrange(arr []int) int {
	largestIndex := -1

	for i := 1; i < len(arr); i++ {
		// Check if current element is not greater than or equal to previous element
		// In other words, check if current element is less than previous element
		if arr[i] < arr[i-1] {
			largestIndex = i
		}
	}

	return largestIndex
}
