package main

import (
	"strings"
)

func FixSpaces(text string) string {
	result := ""
	spaceCount := 0

	for _, char := range text {
		if char == ' ' {
			spaceCount++
		} else {
			// Handle accumulated spaces
			if spaceCount > 0 {
				if spaceCount > 2 {
					result += "-"
				} else {
					result += strings.Repeat("_", spaceCount)
				}
				spaceCount = 0
			}
			result += string(char)
		}
	}

	// Handle trailing spaces
	if spaceCount > 0 {
		if spaceCount > 2 {
			result += "-"
		} else {
			result += strings.Repeat("_", spaceCount)
		}
	}

	return result
}
