package main

import (
	"strings"
)

// Create a function which takes a string representing a file's name, and returns
// 'Yes' if the the file's name is valid, and returns 'No' otherwise.
// A file's name is considered to be valid if and only if all the following conditions
// are met:
// - There should not be more than three digits ('0'-'9') in the file's name.
// - The file's name contains exactly one dot '.'
// - The substring before the dot should not be empty, and it starts with a letter from
// the latin alphapet ('a'-'z' and 'A'-'Z').
// - The substring after the dot should be one of these: ['txt', 'exe', 'dll']
// Examples:
// FileNameCheck("example.txt") # => 'Yes'
// FileNameCheck("1example.dll") # => 'No' (the name should start with a latin alphapet letter)
func FileNameCheck(file_name string) string {
	// Check if there's exactly one dot
	dotCount := strings.Count(file_name, ".")
	if dotCount != 1 {
		return "No"
	}

	// Split by dot
	parts := strings.Split(file_name, ".")
	name := parts[0]
	ext := parts[1]

	// Check if name part is not empty
	if len(name) == 0 {
		return "No"
	}

	// Check if name starts with a latin alphabet letter
	firstChar := rune(name[0])
	if !((firstChar >= 'a' && firstChar <= 'z') || (firstChar >= 'A' && firstChar <= 'Z')) {
		return "No"
	}

	// Check if extension is one of the valid ones
	validExts := map[string]bool{"txt": true, "exe": true, "dll": true}
	if !validExts[ext] {
		return "No"
	}

	// Count digits in the entire file_name
	digitCount := 0
	for _, ch := range file_name {
		if ch >= '0' && ch <= '9' {
			digitCount++
		}
	}

	// Check if there are more than three digits
	if digitCount > 3 {
		return "No"
	}

	return "Yes"
}
