package main

import (
	"strconv"
	"strings"
)

func Simplify(x, n string) bool {
	// Parse fraction x
	xParts := strings.Split(x, "/")
	xNum, _ := strconv.Atoi(xParts[0])
	xDen, _ := strconv.Atoi(xParts[1])

	// Parse fraction n
	nParts := strings.Split(n, "/")
	nNum, _ := strconv.Atoi(nParts[0])
	nDen, _ := strconv.Atoi(nParts[1])

	// Multiply fractions: (xNum/xDen) * (nNum/nDen) = (xNum * nNum) / (xDen * nDen)
	resultNum := xNum * nNum
	resultDen := xDen * nDen

	// Check if result is a whole number (denominator divides numerator evenly)
	return resultNum%resultDen == 0
}
