package main

import (
	"strconv"
)

// Write a function that takes an array of numbers as input and returns
// the number of elements in the array that are greater than 10 and both
// first and last digits of a number are odd (1, 3, 5, 7, 9).
// For example:
// Specialfilter([15, -73, 14, -15]) => 1
// Specialfilter([33, -2, -3, 45, 21, 109]) => 2
func Specialfilter(nums []int) int {
	count := 0

	for _, num := range nums {
		// Check if number is greater than 10
		if num <= 10 {
			continue
		}

		// Convert to string to get digits (use absolute value to handle negatives)
		numStr := strconv.Itoa(num)
		if numStr[0] == '-' {
			numStr = numStr[1:]
		}

		// Get first and last digits
		firstDigit := int(numStr[0] - '0')
		lastDigit := int(numStr[len(numStr)-1] - '0')

		// Check if both first and last digits are odd
		if isOdd(firstDigit) && isOdd(lastDigit) {
			count++
		}
	}

	return count
}

// Helper function to check if a digit is odd
func isOdd(digit int) bool {
	return digit%2 == 1
}
