package main

func GetMaxTriples(n int) int {
	// Create array where a[i] = i*i - i + 1 (1-indexed, but we'll use 0-indexed)
	a := make([]int, n)
	for i := 0; i < n; i++ {
		idx := i + 1 // Convert to 1-indexed for formula
		a[i] = idx*idx - idx + 1
	}

	// Count triples (i, j, k) where i < j < k and (a[i] + a[j] + a[k]) % 3 == 0
	count := 0
	for i := 0; i < n; i++ {
		for j := i + 1; j < n; j++ {
			for k := j + 1; k < n; k++ {
				if (a[i] + a[j] + a[k]) % 3 == 0 {
					count++
				}
			}
		}
	}

	return count
}
