package main

func RightAngleTriangle(a, b, c int) bool {
	// Sort the three sides so that c is the largest
	sides := []int{a, b, c}

	// Simple bubble sort to order sides
	for i := 0; i < len(sides); i++ {
		for j := i + 1; j < len(sides); j++ {
			if sides[i] > sides[j] {
				sides[i], sides[j] = sides[j], sides[i]
			}
		}
	}

	// Check Pythagorean theorem: a² + b² = c²
	// where c is the longest side (hypotenuse)
	return sides[0]*sides[0] + sides[1]*sides[1] == sides[2]*sides[2]
}
