// sumDigits calculates the sum of digits for a given integer `n`.
//
// For positive numbers, it sums the absolute values of the digits.
// Example: sumDigits(123) = 1 + 2 + 3 = 6.
//
// For negative numbers, it treats the first significant digit as negative
// and subsequent digits as positive, as specified in the problem description.
// Example: sumDigits(-123) = -1 + 2 + 3 = 4.
// Example: sumDigits(-11) = -1 + 1 = 0.
// Example: sumDigits(-1) = -1.
//
// If the number is 0, the sum of digits is 0.
func sumDigits(n int) int {
	if n == 0 {
		return 0
	}

	// Convert the number to its string representation to easily handle the negative sign.
	s := strconv.Itoa(n)
	totalSum := 0
	isNegative := false
	startIndex := 0 // The index in the string 's' where digit characters start.

	// Check if the number is negative by looking at the first character of its string representation.
	if s[0] == '-' {
		isNegative = true
		startIndex = 1 // If negative, skip the '-' character when parsing digits.
	}

	// Iterate through the characters that represent the numerical digits.
	for i := startIndex; i < len(s); i++ {
		// Convert the character to its integer value.
		// strconv.Atoi will not return an error here as we are iterating over digits of a valid number.
		digit, _ := strconv.Atoi(string(s[i]))

		// Apply the special rule for negative numbers: the first numerical digit is negative.
		if i == startIndex && isNegative {
			totalSum -= digit // Subtract to make it negative.
		} else {
			totalSum += digit // For all other digits (including all digits of positive numbers), add them.
		}
	}
	return totalSum
}

// CountNums takes an array of integers and returns the number of elements
