// Given a list of strings, where each string consists of only digits, return a list.
// Each element i of the output should be "the number of odd elements in the
// string i of the input." where all the i's should be replaced by the number
// of odd digits in the i'th string of the input.
//
// >>> OddCount(['1234567'])
// ["the number of odd elements 4n the str4ng 4 of the 4nput."]
// >>> OddCount(['3',"11111111"])
// ["the number of odd elements 1n the str1ng 1 of the 1nput.",
// "the number of odd elements 8n the str8ng 8 of the 8nput."]
func OddCount(lst []string) []string {
	result := make([]string, 0, len(lst)) // Pre-allocate capacity for efficiency

	for _, s := range lst {
		oddDigitsCount := 0
		for _, r := range s { // Iterate over runes (characters) in the string
			// Convert rune to integer digit. '0' through '9' are contiguous.
			digit := int(r - '0')
			if digit%2 != 0 { // Check if the digit is odd
				oddDigitsCount++
			}
		}

		// Format the output string using the counted odd digits.
		// The prompt specifies the format as:
		// "the number of odd elements <oddCount>n the str<oddCount>ng <oddCount> of the <oddCount>nput."
		outputString := fmt.Sprintf("the number of odd elements %dn the str%dng %d of the %dnput.",
			oddDigitsCount, oddDigitsCount, oddDigitsCount, oddDigitsCount)
		result = append(result, outputString)
	}

	return result
}

