package main

// Given a non-empty list of integers, return the sum of all of the odd elements that are in even positions.
//
// Examples
// Solution([5, 8, 7, 1]) ==> 12
// Solution([3, 3, 3, 3, 3]) ==> 9
// Solution([30, 13, 24, 321]) ==>0
func Solution(lst []int) int {
	sum := 0
	for i, v := range lst {
		// Check if the current position (index) is even (0-indexed)
		// and if the element at that position is odd.
		if i%2 == 0 && v%2 != 0 {
			sum += v
		}
	}
	return sum
}
