package main

// Given a positive integer n, return the product of the odd Digits.
// Return 0 if all Digits are even.
// For example:
// Digits(1)  == 1
// Digits(4)  == 0
// Digits(235) == 15
func Digits(n int) int {
	product := 1
	foundOddDigit := false

	// Loop until n becomes 0, processing one digit at a time
	for n > 0 {
		digit := n % 10 // Get the last digit

		// Check if the digit is odd
		if digit%2 != 0 {
			product *= digit     // Multiply with the product
			foundOddDigit = true // Set the flag that an odd digit was found
		}

		n /= 10 // Remove the last digit
	}

	// If no odd digits were found, return 0 as per requirement
	if !foundOddDigit {
		return 0
	}

	// Otherwise, return the calculated product
	return product
}
