package main

import (
	"strings"
)

// isPrime checks if a given integer n is a prime number.
// A prime number is a natural number greater than 1 that has no positive divisors other than 1 and itself.
func isPrime(n int) bool {
	if n <= 1 {
		return false // Numbers less than or equal to 1 are not prime
	}
	if n == 2 {
		return true // 2 is the only even prime number
	}
	if n%2 == 0 {
		return false // All other even numbers are not prime
	}
	// Check for odd divisors from 3 up to the square root of n
	// We increment by 2 because we only need to check odd divisors
	for i := 3; i*i <= n; i += 2 {
		if n%i == 0 {
			return false // If n is divisible by i, it's not prime
		}
	}
	return true // If no divisors were found, n is prime
}

// WordsInSentence processes a sentence and returns a new string
// containing only the words whose lengths are prime numbers,
// preserving their original order.
func WordsInSentence(sentence string) string {
	// Split the sentence into individual words using space as a delimiter.
	// strings.Fields handles multiple spaces and leading/trailing spaces correctly.
	words := strings.Fields(sentence)

	var primeLengthWords []string // Slice to store words with prime lengths

	// Iterate over each word in the sentence
	for _, word := range words {
		// Check if the length of the current word is a prime number
		if isPrime(len(word)) {
			// If the length is prime, add the word to our result slice
			primeLengthWords = append(primeLengthWords, word)
		}
	}

	// Join the selected words back into a single string,
	// separated by a single space.
	return strings.Join(primeLengthWords, " ")
}
