package main

import (
	"strconv"
)

// Write a function that takes an array of numbers as input and returns
// the number of elements in the array that are greater than 10 and both
// first and last digits of a number are odd (1, 3, 5, 7, 9).
// For example:
// Specialfilter([15, -73, 14, -15]) => 1
// Specialfilter([33, -2, -3, 45, 21, 109]) => 2
func Specialfilter(nums []int) int {
	count := 0

	for _, num := range nums {
		// Condition 1: The number must be greater than 10.
		// This also implicitly handles negative numbers and single-digit positive numbers,
		// as they will not satisfy this condition.
		if num <= 10 {
			continue
		}

		// Convert the number to its string representation to easily extract digits.
		// Since num > 10, s will always have at least two characters (e.g., "11").
		s := strconv.Itoa(num)

		// Extract the first digit.
		// s[0] gives the byte value of the character. Subtract '0' to get the integer value.
		firstDigit := int(s[0] - '0')

		// Extract the last digit.
		lastDigit := int(s[len(s)-1] - '0')

		// Condition 2: The first digit must be odd.
		isFirstDigitOdd := (firstDigit%2 != 0)

		// Condition 3: The last digit must be odd.
		isLastDigitOdd := (lastDigit%2 != 0)

		// If all three conditions are met, increment the count.
		if isFirstDigitOdd && isLastDigitOdd {
			count++
		}
	}

	return count
}
