package main

// isPrime checks if a number n is prime.
// A prime number is a natural number greater than 1 that has no positive
// divisors other than 1 and itself.
func isPrime(n int) bool {
	// Numbers less than or equal to 1 are not prime.
	if n <= 1 {
		return false
	}
	// 2 is the only even prime number.
	if n == 2 {
		return true
	}
	// All other even numbers are not prime.
	if n%2 == 0 {
		return false
	}
	// Check for odd divisors from 3 up to the square root of n.
	// If n has a divisor greater than its square root, it must also
	// have a divisor smaller than its square root.
	for i := 3; i*i <= n; i += 2 {
		if n%i == 0 {
			return false
		}
	}
	// If no divisors were found, n is prime.
	return true
}

// XOrY returns the value of x if n is a prime number and returns the value of y otherwise.
func XOrY(n, x, y int) int {
	if isPrime(n) {
		return x
	}
	return y
}
