package main

import (
	"strings"
)

// CycpatternCheck determines if the second word (b) or any of its rotations
// is a substring in the first word (a).
//
// Examples:
// CycpatternCheck("abcd","abd") => false
// CycpatternCheck("hello","ell") => true
// CycpatternCheck("whassup","psus") => false
// CycpatternCheck("abab","baa") => true
// CycpatternCheck("efef","eeff") => false
// CycpatternCheck("himenss","simen") => true
func CycpatternCheck(a, b string) bool {
	lenA := len(a)
	lenB := len(b)

	// Edge case 1: If 'b' is empty, it's considered a substring of any string 'a' (including an empty 'a').
	if lenB == 0 {
		return true
	}

	// Edge case 2: If 'a' is empty but 'b' is not, 'b' cannot be a substring of 'a'.
	if lenA == 0 { // This implies lenB > 0 due to the previous check
		return false
	}

	// Edge case 3: If 'b' is longer than 'a', 'b' (or any of its rotations) cannot be a substring of 'a'.
	if lenB > lenA {
		return false
	}

	// To efficiently check all rotations of 'b':
	// Concatenate 'b' with itself (e.g., if b = "abc", bDoubled = "abcabc").
	// Any rotation of 'b' will appear as a substring of 'bDoubled' with length `lenB`.
	// For example, for "abcabc":
	// "abc" is bDoubled[0:3]
	// "bca" is bDoubled[1:4]
	// "cab" is bDoubled[2:5]
	// We then iterate through these rotations and check if any of them is a substring of 'a'.
	bDoubled := b + b

	// Iterate through all possible starting points for a rotation within bDoubled.
	// There are `lenB` unique rotations to check (from index 0 up to lenB-1).
	for i := 0; i < lenB; i++ {
		// Extract a rotation of 'b'.
		// A rotation starts at index 'i' in bDoubled and has length `lenB`.
		rotation := bDoubled[i : i+lenB]

		// Check if this specific rotation is a substring of 'a'.
		if strings.Contains(a, rotation) {
			return true // Found a rotation that is a substring, so we can return true immediately.
		}
	}

	// If the loop completes, it means no rotation of 'b' was found as a substring in 'a'.
	return false
}
