package main

import (
	"strconv"
)

// Given an integer. return a tuple that has the number of even and odd digits respectively.
// 
// Example:
// EvenOddCount(-12) ==> (1, 1)
// EvenOddCount(123) ==> (1, 2)
func EvenOddCount(num int) [2]int {
	counts := [2]int{0, 0} // counts[0] for even, counts[1] for odd

	// Handle negative numbers by taking the absolute value.
	// This ensures we process digits regardless of the sign.
	if num < 0 {
		num = -num
	}

	// Convert the (absolute) number to its string representation.
	// This allows easy iteration over its digits.
	numStr := strconv.Itoa(num)

	// Iterate over each character (digit) in the string.
	for _, char := range numStr {
		// Convert the character digit to an integer.
		// This works because digits '0' through '9' are consecutive in ASCII.
		digit := int(char - '0')

		// Check if the digit is even or odd and increment the respective counter.
		if digit%2 == 0 {
			counts[0]++ // Even digit count
		} else {
			counts[1]++ // Odd digit count
		}
	}

	return counts
}
