package main

func MakeAPile(n int) []int {
    stones := make([]int, n)
    for i := 0; i < n; i++ {
        if i == 0 {
            stones[i] = n
        } else {
            if stones[i-1]%2 == 0 {
                stones[i] = stones[i-1] + 1 // next odd number
            } else {
                stones[i] = stones[i-1] + 2 // next even number
            }
        }
    }
    return stones
}
