package main

func ChooseNum(x, y int) int {
    if x > y {
        return -1
    }

    // Adjust x to be the closest even number less than or equal to y
    if y%2 != 0 {
        y--
    }

    // If x is odd, adjust it to the next even number
    if x%2 != 0 {
        x++
    }

    if x > y {
        return -1
    }

    return y
}
