package main

import (
    "bytes"
)

func GetClosestVowel(word string) string {
    vowels := "AEIOUaeiou"
    n := len(word)

    // Iterate from the end to find the closest vowel between consonants
    for i := n - 1; i > 0; i-- {
        if bytes.IndexByte([]byte(vowels), word[i]) != -1 {
            // Check if there's a consonant before and after the vowel
            if i > 0 && i < n-1 && 
            bytes.IndexByte([]byte("BCDFGHJKLMNPQRSTVWXYZbcdfghjklmnpqrstvwxyz"), word[i-1]) != -1 &&
            bytes.IndexByte([]byte("BCDFGHJKLMNPQRSTVWXYZbcdfghjklmnpqrstvwxyz"), word[i+1]) != -1 {
                return string(word[i])
            }
        }
    }

    return ""
}
