package main

import (
    "strings"
    "unicode"
)

func FileNameCheck(file_name string) string {
    // Split the filename into name and extension
    parts := strings.Split(file_name, ".")

    // There should be exactly one dot
    if len(parts) != 2 {
        return "No"
    }

    name, ext := parts[0], parts[1]

    // The name should not be empty and should start with a letter
    if len(name) == 0 || !unicode.IsLetter(rune(name[0])) {
        return "No"
    }

    // Count digits in the name
    digitCount := 0
    for _, ch := range name {
        if unicode.IsDigit(ch) {
            digitCount++
        }
    }

    // There should not be more than three digits
    if digitCount > 3 {
        return "No"
    }

    // Check extension for allowed values
    allowedExtensions := map[string]bool{
        "txt": true,
        "exe": true,
        "dll": true,
    }

    if !allowedExtensions[strings.ToLower(ext)] {
        return "No"
    }

    // Valid filename
    return "Yes"
}
