package main

import (
    "strings"
)

// function to check if a number is prime
func isPrime(n int) bool {
    if n <= 1 {
        return false
    }
    for i := 2; i*i <= n; i++ {
        if n%i == 0 {
            return false
        }
    }
    return true
}

// WordsInSentence returns words from the original sentence whose lengths are prime numbers
func WordsInSentence(sentence string) string {
    words := strings.Fields(sentence)
    var result []string

    for _, word := range words {
        if isPrime(len(word)) {
            result = append(result, word)
        }
    }

    return strings.Join(result, " ")
}
