package main

import (
	"fmt"
	"math/rand"
	"sync"
	"time"
)

type ZoneInfo struct {
	FailureDomain string
	Region        string
}

type CloudZoneService struct {
	mu                sync.Once
	cachedDomain      string
	cacheErr          error
	region            string
	initializationMux sync.Mutex
	initialized      bool
}

func NewCloudZoneService(region string) *CloudZoneService {
	return &CloudZoneService{
		region: region,
	}
}

func fetchFailureDomain() (string, error) {
	time.Sleep(100 * time.Millisecond)

	if rand.Float64() < 0.3 {
		return "", fmt.Errorf("network error: failed to fetch failure domain")
	}

	zones := []string{"zone-1", "zone-2", "zone-3", "zone-4"}
	return zones[rand.Intn(len(zones))], nil
}

func (s *CloudZoneService) GetZone() (*ZoneInfo, error) {
	var domain string
	var err error

	s.mu.Do(func() {
		s.initializationMux.Lock()
		defer s.initializationMux.Unlock()

		if s.initialized {
			return
		}

		domain, err = fetchFailureDomain()
		if err == nil {
			s.cachedDomain = domain
			s.initialized = true
		}
		s.cacheErr = err
	})

	if s.cacheErr != nil {
		return nil, s.cacheErr
	}

	return &ZoneInfo{
		FailureDomain: s.cachedDomain,
		Region:        s.region,
	}, nil
}

func main() {
	rand.Seed(time.Now().UnixNano())

	service := NewCloudZoneService("us-west")

	var wg sync.WaitGroup
	resultsMux := sync.Mutex{}
	successCount := 0
	errorCount := 0
	fetchCount := 0

	for i := 1; i <= 10; i++ {
		wg.Add(1)
		go func(goroutineID int) {
			defer wg.Done()

			for j := 1; j <= 3; j++ {
				zone, err := service.GetZone()

				resultsMux.Lock()
				if err != nil {
					fmt.Printf("Goroutine %d, Call %d: ERROR - %v\n", goroutineID, j, err)
					errorCount++
				} else {
					fmt.Printf("Goroutine %d, Call %d: SUCCESS - Domain: %s, Region: %s\n",
						goroutineID, j, zone.FailureDomain, zone.Region)
					successCount++
				}
				fetchCount++
				resultsMux.Unlock()

				time.Sleep(time.Duration(rand.Intn(50)) * time.Millisecond)
			}
		}(i)
	}

	wg.Wait()

	fmt.Println("\n=== Summary ===")
	fmt.Printf("Total calls: %d\n", fetchCount)
	fmt.Printf("Successful calls: %d\n", successCount)
	fmt.Printf("Failed calls: %d\n", errorCount)
	fmt.Println("All goroutines completed successfully!")
}

