package main

import (
	"fmt"
	"sync"
	"time"
)

type ResourceUpdate struct {
	ResourceType string
	ResourceID   string
	UsageValue   float64
}

type ResourceMonitor struct {
	highPriorityQueue chan ResourceUpdate
	lowPriorityQueue  chan ResourceUpdate

	mu                    sync.RWMutex
	configuredResources   map[string]bool
	resourceData          map[string]map[string][]float64

	reconfigMutex         sync.Mutex
	reconfigSignal        chan struct{}
	workersWaiting        int
	totalWorkers          int

	workerWg              sync.WaitGroup
	shutdownOnce          sync.Once
	shutdownSignal        chan struct{}

	highPriorityWorkers   int
	lowPriorityWorkers    int
}

func NewResourceMonitor(initialResources []string, workersPerQueue int) *ResourceMonitor {
	rm := &ResourceMonitor{
		highPriorityQueue:  make(chan ResourceUpdate, 1000),
		lowPriorityQueue:   make(chan ResourceUpdate, 1000),
		configuredResources: make(map[string]bool),
		resourceData:       make(map[string]map[string][]float64),
		reconfigSignal:     make(chan struct{}),
		shutdownSignal:     make(chan struct{}),
		highPriorityWorkers: workersPerQueue,
		lowPriorityWorkers:  workersPerQueue,
		totalWorkers:       workersPerQueue * 2,
	}

	for _, resource := range initialResources {
		rm.configuredResources[resource] = true
		rm.resourceData[resource] = make(map[string][]float64)
	}

	rm.startWorkers()

	return rm
}

func (rm *ResourceMonitor) startWorkers() {
	for i := 0; i < rm.highPriorityWorkers; i++ {
		rm.workerWg.Add(1)
		go rm.workerRoutine("high", i, rm.highPriorityQueue)
	}

	for i := 0; i < rm.lowPriorityWorkers; i++ {
		rm.workerWg.Add(1)
		go rm.workerRoutine("low", i, rm.lowPriorityQueue)
	}
}

func (rm *ResourceMonitor) workerRoutine(priority string, id int, queue chan ResourceUpdate) {
	defer rm.workerWg.Done()

	for {
		select {
		case <-rm.shutdownSignal:
			return
		case update := <-queue:
			rm.reconfigMutex.Lock()
			rm.workersWaiting++
			if rm.workersWaiting == rm.totalWorkers {
				close(rm.reconfigSignal)
				rm.reconfigSignal = make(chan struct{})
			}
			rm.reconfigMutex.Unlock()

			<-rm.reconfigSignal

			rm.reconfigMutex.Lock()
			rm.workersWaiting--
			rm.reconfigMutex.Unlock()

			rm.mu.RLock()
			isConfigured := rm.configuredResources[update.ResourceType]
			rm.mu.RUnlock()

			if isConfigured {
				rm.mu.Lock()
				if _, exists := rm.resourceData[update.ResourceType]; !exists {
					rm.resourceData[update.ResourceType] = make(map[string][]float64)
				}
				rm.resourceData[update.ResourceType][update.ResourceID] = append(
					rm.resourceData[update.ResourceType][update.ResourceID],
					update.UsageValue,
				)
				rm.mu.Unlock()

				fmt.Printf("[%s-Worker-%d] Processed: %s/%s = %.2f\n",
					priority, id, update.ResourceType, update.ResourceID, update.UsageValue)
			}
		}
	}
}

func (rm *ResourceMonitor) SubmitHighPriority(update ResourceUpdate) error {
	select {
	case <-rm.shutdownSignal:
		return fmt.Errorf("monitor is shutdown")
	case rm.highPriorityQueue <- update:
		return nil
	default:
		return fmt.Errorf("high priority queue full")
	}
}

func (rm *ResourceMonitor) SubmitLowPriority(update ResourceUpdate) error {
	select {
	case <-rm.shutdownSignal:
		return fmt.Errorf("monitor is shutdown")
	case rm.lowPriorityQueue <- update:
		return nil
	default:
		return fmt.Errorf("low priority queue full")
	}
}

func (rm *ResourceMonitor) Reconfigure(newResources []string) {
	rm.reconfigMutex.Lock()
	defer rm.reconfigMutex.Unlock()

	fmt.Println("\n=== RECONFIGURATION INITIATED ===")
	fmt.Printf("Pausing %d workers...\n", rm.totalWorkers)

	rm.mu.Lock()
	newConfiguredResources := make(map[string]bool)
	for _, resource := range newResources {
		newConfiguredResources[resource] = true
		if _, exists := rm.resourceData[resource]; !exists {
			rm.resourceData[resource] = make(map[string][]float64)
		}
	}

	resourcesRemoved := []string{}
	for resource := range rm.configuredResources {
		if !newConfiguredResources[resource] {
			resourcesRemoved = append(resourcesRemoved, resource)
		}
	}

	rm.configuredResources = newConfiguredResources
	rm.mu.Unlock()

	fmt.Printf("Updated configuration - Monitoring: %v\n", newResources)
	if len(resourcesRemoved) > 0 {
		fmt.Printf("Removed from monitoring: %v\n", resourcesRemoved)
	}

	time.Sleep(100 * time.Millisecond)

	fmt.Println("Resuming all workers...")
	fmt.Println("=== RECONFIGURATION COMPLETE ===\n")
}

func (rm *ResourceMonitor) GetResourceStats() map[string]map[string][]float64 {
	rm.mu.RLock()
	defer rm.mu.RUnlock()

	stats := make(map[string]map[string][]float64)
	for resource, ids := range rm.resourceData {
		stats[resource] = make(map[string][]float64)
		for id, values := range ids {
			valueCopy := make([]float64, len(values))
			copy(valueCopy, values)
			stats[resource][id] = valueCopy
		}
	}
	return stats
}

func (rm *ResourceMonitor) Shutdown() {
	rm.shutdownOnce.Do(func() {
		fmt.Println("\nInitiating graceful shutdown...")
		close(rm.shutdownSignal)
		rm.workerWg.Wait()
		fmt.Println("All workers stopped")
	})
}

