package main

import (
	"fmt"
	"sync"
	"time"
)

type DeviceState int

const (
	StateCreated DeviceState = iota
	StateMounted
	StateUnmounted
	StateDeleted
)

type Device struct {
	mu        sync.RWMutex
	ID        string
	ParentID  string
	MountPath string
	MountCount int
	State     DeviceState
	CreatedAt time.Time
}

type DeviceManager struct {
	mu      sync.RWMutex
	devices map[string]*Device
	startTime time.Time
}

func NewDeviceManager() *DeviceManager {
	dm := &DeviceManager{
		devices:   make(map[string]*Device),
		startTime: time.Now(),
	}
	dm.devices["base"] = &Device{
		ID:        "base",
		ParentID:  "",
		State:     StateMounted,
		MountCount: 1,
		CreatedAt: dm.startTime,
	}
	return dm
}

func (dm *DeviceManager) timeStr() string {
	elapsed := time.Since(dm.startTime)
	return fmt.Sprintf("[%03dms]", elapsed.Milliseconds())
}

func (dm *DeviceManager) AddDevice(deviceID, parentID string) error {
	dm.mu.Lock()
	defer dm.mu.Unlock()

	if _, exists := dm.devices[deviceID]; exists {
		fmt.Printf("%s AddDevice %s with parent %s - Failed (device already exists)\n", dm.timeStr(), deviceID, parentID)
		return fmt.Errorf("device already exists: %s", deviceID)
	}

	if _, exists := dm.devices[parentID]; !exists {
		fmt.Printf("%s AddDevice %s with parent %s - Failed (parent not found)\n", dm.timeStr(), deviceID, parentID)
		return fmt.Errorf("parent device not found: %s", parentID)
	}

	device := &Device{
		ID:        deviceID,
		ParentID:  parentID,
		State:     StateCreated,
		MountCount: 0,
		CreatedAt: time.Now(),
	}
	dm.devices[deviceID] = device
	fmt.Printf("%s AddDevice %s with parent %s - Success\n", dm.timeStr(), deviceID, parentID)
	return nil
}

func (dm *DeviceManager) DeleteDevice(deviceID string) error {
	dm.mu.Lock()
	device, exists := dm.devices[deviceID]
	if !exists {
		dm.mu.Unlock()
		fmt.Printf("%s DeleteDevice %s - Failed (device not found)\n", dm.timeStr(), deviceID)
		return fmt.Errorf("device not found: %s", deviceID)
	}

	device.mu.Lock()
	if device.MountCount > 0 {
		device.mu.Unlock()
		dm.mu.Unlock()
		fmt.Printf("%s DeleteDevice %s - Failed (device is mounted)\n", dm.timeStr(), deviceID)
		return fmt.Errorf("cannot delete mounted device: %s", deviceID)
	}
	device.State = StateDeleted
	device.mu.Unlock()

	delete(dm.devices, deviceID)
	dm.mu.Unlock()

	fmt.Printf("%s DeleteDevice %s - Success\n", dm.timeStr(), deviceID)
	return nil
}

func (dm *DeviceManager) MountDevice(deviceID, mountPath string) error {
	dm.mu.RLock()
	device, exists := dm.devices[deviceID]
	dm.mu.RUnlock()

	if !exists {
		fmt.Printf("%s MountDevice %s at %s - Failed (device not found)\n", dm.timeStr(), deviceID, mountPath)
		return fmt.Errorf("device not found: %s", deviceID)
	}

	device.mu.Lock()
	defer device.mu.Unlock()

	if device.State == StateDeleted {
		fmt.Printf("%s MountDevice %s at %s - Failed (device deleted)\n", dm.timeStr(), deviceID, mountPath)
		return fmt.Errorf("device is deleted: %s", deviceID)
	}

	time.Sleep(100 * time.Millisecond)

	if device.MountCount == 0 {
		device.MountPath = mountPath
	} else if device.MountPath != mountPath {
		fmt.Printf("%s MountDevice %s at %s - Failed (already mounted at %s)\n", dm.timeStr(), deviceID, mountPath, device.MountPath)
		return fmt.Errorf("device already mounted at different path: %s", device.MountPath)
	}

	device.MountCount++
	device.State = StateMounted

	fmt.Printf("%s MountDevice %s at %s - Success (mount count: %d)\n", dm.timeStr(), deviceID, mountPath, device.MountCount)
	return nil
}

func (dm *DeviceManager) UnmountDevice(deviceID string) error {
	dm.mu.RLock()
	device, exists := dm.devices[deviceID]
	dm.mu.RUnlock()

	if !exists {
		fmt.Printf("%s UnmountDevice %s - Failed (device not found)\n", dm.timeStr(), deviceID)
		return fmt.Errorf("device not found: %s", deviceID)
	}

	device.mu.Lock()
	defer device.mu.Unlock()

	if device.MountCount == 0 {
		fmt.Printf("%s UnmountDevice %s - Failed (device not mounted)\n", dm.timeStr(), deviceID)
		return fmt.Errorf("device is not mounted: %s", deviceID)
	}

	time.Sleep(100 * time.Millisecond)

	device.MountCount--
	if device.MountCount == 0 {
		device.State = StateUnmounted
		fmt.Printf("%s UnmountDevice %s - Success (mount count: 0)\n", dm.timeStr(), deviceID)
	} else {
		fmt.Printf("%s UnmountDevice %s - Success (mount count: %d)\n", dm.timeStr(), deviceID, device.MountCount)
	}

	return nil
}

func (dm *DeviceManager) GetDeviceStatus(deviceID string) (map[string]interface{}, error) {
	dm.mu.RLock()
	device, exists := dm.devices[deviceID]
	dm.mu.RUnlock()

	if !exists {
		fmt.Printf("%s GetDeviceStatus %s - Failed (device not found)\n", dm.timeStr(), deviceID)
		return nil, fmt.Errorf("device not found: %s", deviceID)
	}

	device.mu.RLock()
	defer device.mu.RUnlock()

	status := map[string]interface{}{
		"ID":         device.ID,
		"ParentID":   device.ParentID,
		"MountPath":  device.MountPath,
		"MountCount": device.MountCount,
		"State":      device.State,
	}

	stateStr := "unknown"
	if device.State == StateCreated {
		stateStr = "created"
	} else if device.State == StateMounted {
		stateStr = "mounted"
	} else if device.State == StateUnmounted {
		stateStr = "unmounted"
	} else if device.State == StateDeleted {
		stateStr = "deleted"
	}

	fmt.Printf("%s GetDeviceStatus %s - Success (%s, mount count: %d)\n", dm.timeStr(), deviceID, stateStr, device.MountCount)
	return status, nil
}

