		manager.MountDevice("dev1", "/mnt/dev1")
		time.Sleep(2 * time.Second)
		manager.UnmountDevice("dev1")
		fmt.Printf("%s Goroutine A: Completed\n", manager.timeStr())
	}()

	time.Sleep(500 * time.Millisecond)

	wg.Add(1)
	go func() {
		defer wg.Done()
		fmt.Printf("%s Goroutine B: Getting device status\n", manager.timeStr())
		manager.GetDeviceStatus("dev1")
		fmt.Printf("%s Goroutine B: Completed\n", manager.timeStr())
	}()

	wg.Add(1)
	go func() {
		defer wg.Done()
		fmt.Printf("%s Goroutine C: Starting mount operation\n", manager.timeStr())
		manager.MountDevice("dev1", "/mnt/dev1")
		time.Sleep(500 * time.Millisecond)
		manager.UnmountDevice("dev1")
		fmt.Printf("%s Goroutine C: Completed\n", manager.timeStr())
