package main

import (
	"fmt"
	"math/rand"
	"sync"
	"time"
)

type Container struct {
	ID        string
	Name      string
	Running   bool
	ExitCode  int
}

type ContainerManager struct {
	containers map[string]*Container
	mu         sync.RWMutex
}

func NewContainerManager() *ContainerManager {
	return &ContainerManager{
		containers: make(map[string]*Container),
	}
}

func (cm *ContainerManager) StartContainer(name string) string {
	id := generateID()
	container := &Container{
		ID:      id,
		Name:    name,
		Running: true,
	cm.mu.Lock()
	cm.containers[id] = container
	cm.mu.Unlock()

	fmt.Printf("Started container: %s %s\n", id, name)
	return id
}

func (cm *ContainerManager) StopContainer(id string, exitCode int) bool {
	cm.mu.Lock()
	defer cm.mu.Unlock()

	container, exists := cm.containers[id]
	if !exists || !container.Running {
		return false
	}

	container.Running = false
	container.ExitCode = exitCode
	fmt.Printf("Stopped container: %s with exit code: %d\n", id, exitCode)
	return true
}

func (cm *ContainerManager) ListContainers(filterExitCode *int) []*Container {
	cm.mu.RLock()
	defer cm.mu.RUnlock()

	var result []*Container
	for _, container := range cm.containers {
		if filterExitCode == nil {
			result = append(result, container)
		} else if !container.Running && container.ExitCode == *filterExitCode {
			result = append(result, container)
		}
	}
	return result
}

func generateID() string {
	const charset = "abcdefghijklmnopqrstuvwxyz0123456789"
	b := make([]byte, 12)
	for i := range b {
		b[i] = charset[rand.Intn(len(charset))]
	}
	return string(b)
}

func main() {
	rand.Seed(time.Now().UnixNano())
	cm := NewContainerManager()
	var wg sync.WaitGroup
	containerIDs := make([]string, 10)

	// Start 10 containers concurrently
	for i := 0; i < 10; i++ {
		wg.Add(1)
		go func(i int) {
			defer wg.Done()
			name := fmt.Sprintf("container-%d", i)
			id := cm.StartContainer(name)
			containerIDs[i] = id
		}(i)
	}

	// Wait for all containers to start
	wg.Wait()

	// Start 5 goroutines to continuously list containers
	stopChan := make(chan struct{})
	for i := 0; i < 5; i++ {
		wg.Add(1)
		go func() {
			defer wg.Done()
			for {
				select {
				case <-stopChan:
					return
				default:
					code0 := 0
					containers0 := cm.ListContainers(&code0)
					ids0 := make([]string, 0, len(containers0))
					for _, c := range containers0 {
						ids0 = append(ids0, c.ID)
					}
					fmt.Printf("Listing containers with exit code 0: %v\n", ids0)

					code1 := 1
					containers1 := cm.ListContainers(&code1)
					ids1 := make([]string, 0, len(containers1))
					for _, c := range containers1 {
						ids1 = append(ids1, c.ID)
					}
					fmt.Printf("Listing containers with exit code 1: %v\n", ids1)

					time.Sleep(100 * time.Millisecond)
				}
			}
		}()
	}

	// Stop containers concurrently (5 with exit code 0, 5 with exit code 1)
	for i := 0; i < 10; i++ {
		wg.Add(1)
		go func(i int) {
			defer wg.Done()
			exitCode := 0
