	close(stopChan)

	// Final check
	code0 := 0
	containers0 := cm.ListContainers(&code0)
	code1 := 1
	containers1 := cm.ListContainers(&code1)

	fmt.Printf("Final count - Exit code 0: %d, Exit code 1: %d\n", len(containers0), len(containers1))
	fmt.Println("All operations completed successfully!")
