					for _, c := range containers0 {
						ids0 = append(ids0, c.ID)
					}
					fmt.Printf("Listing containers with exit code 0: %v\n", ids0)

					code1 := 1
					containers1 := cm.ListContainers(&code1)
					ids1 := make([]string, 0, len(containers1))
					for _, c := range containers1 {
						ids1 = append(ids1, c.ID)
					}
					fmt.Printf("Listing containers with exit code 1: %v\n", ids1)

					time.Sleep(100 * time.Millisecond)
				}
			}
		}()
	}

	// Stop containers concurrently (5 with exit code 0, 5 with exit code 1)
	for i := 0; i < 10; i++ {
		wg.Add(1)
		go func(i int) {
			defer wg.Done()
			exitCode := 0
			if i >= 5 {
				exitCode = 1
			}
			cm.StopContainer(containerIDs[i], exitCode)
		}(i)
	}

	// Wait for all stop operations to complete
	wg.Wait()

	// Stop the listing goroutines
	close(stopChan)

	// Final check
	code0 := 0
	containers0 := cm.ListContainers(&code0)
	code1 := 1
	containers1 := cm.ListContainers(&code1)

	fmt.Printf("Final count - Exit code 0: %d, Exit code 1: %d\n", len(containers0), len(containers1))
	fmt.Println("All operations completed successfully!")
}
