package main

import (
    "fmt"
    "math/rand"
    "sync"
    "time"
)

// ZoneInfo represents zone information
type ZoneInfo struct {
    FailureDomain string
    Region       string
}

// zoneService is a service that provides zone information
type zoneService struct {
    mu       sync.RWMutex
    zoneInfo *ZoneInfo
    err      error
}

// NewZoneService returns a new zone service
func NewZoneService() *zoneService {
    return &zoneService{}
}

// fetchFailureDomain simulates a network call to fetch a failure domain
func fetchFailureDomain() (string, error) {
    time.Sleep(100 * time.Millisecond)
    if rand.Intn(10) < 3 {
        return "", fmt.Errorf("network failure")
    }
    return fmt.Sprintf("zone-%d", rand.Intn(10)+1), nil
}

// GetZone returns the zone information, fetching it if necessary
func (s *zoneService) GetZone() (*ZoneInfo, error) {
    s.mu.RLock()
    if s.zoneInfo != nil {
        s.mu.RUnlock()
        return s.zoneInfo, nil
    }
    s.mu.RUnlock()

    s.mu.Lock()
    defer s.mu.Unlock()

    if s.zoneInfo != nil {
        return s.zoneInfo, nil
    }

    failureDomain, err := fetchFailureDomain()
    if err != nil {
        s.err = err
        return nil, err
    }

    s.zoneInfo = &ZoneInfo{
        FailureDomain: failureDomain,
        Region:       "us-west",
    }
    return s.zoneInfo, nil
}

func main() {
    rand.Seed(time.Now().UnixNano())
    service := NewZoneService()

    var wg sync.WaitGroup
    for i := 0; i < 10; i++ {
        wg.Add(1)
        go func(id int) {
            defer wg.Done()
            for j := 0; j < 3; j++ {
                zoneInfo, err := service.GetZone()
                if err != nil {
                    fmt.Printf("goroutine %d, call %d: error = %v\n", id, j+1, err)
                } else {
                    fmt.Printf("goroutine %d, call %d: zoneInfo = %+v\n", id, j+1, zoneInfo)
                }
            }
        }(i + 1)
    }
    wg.Wait()
}

