package main

import (
    "fmt"
    "math/rand"
    "sync"
    "time"
)

// Task represents a task with a unique ID and a function to execute
type Task struct {
    id     int
    fn     func()
    started bool
    done    bool
}

// Scheduler manages and executes tasks across multiple workers
type Scheduler struct {
    workerCount int
    taskQueue   chan Task
    workers     []*worker
    wg          sync.WaitGroup
    mu          sync.Mutex
    runningTasks int
    stopped     bool
}

// NewScheduler returns a new scheduler with the given worker count
func NewScheduler(workerCount int) *Scheduler {
    s := &Scheduler{
        workerCount: workerCount,
        taskQueue:   make(chan Task),
        stopped:     false,
    }
    s.startWorkers()
    return s
}

// startWorkers starts the worker goroutines
func (s *Scheduler) startWorkers() {
    for i := 0; i < s.workerCount; i++ {
        w := &worker{
            taskQueue: s.taskQueue,
            stopped:   make(chan struct{}),
        }
        s.workers = append(s.workers, w)
        s.wg.Add(1)
        go func() {
            w.run()
            s.wg.Done()
        }()
    }
}

// SubmitTask submits a new task for execution
func (s *Scheduler) SubmitTask(task Task) {
    s.mu.Lock()
    if s.stopped {
        s.mu.Unlock()
        return
    }
    s.runningTasks++
    s.mu.Unlock()
    s.taskQueue <- task
}

// Stop stops the scheduler gracefully
func (s *Scheduler) Stop() {
    s.mu.Lock()
    s.stopped = true
    s.mu.Unlock()
    close(s.taskQueue)
    s.wg.Wait()
}

// RunningTasks returns the count of currently running tasks
func (s *Scheduler) RunningTasks() int {
    s.mu.Lock()
    defer s.mu.Unlock()
    return s.runningTasks
}

// worker represents a worker goroutine
type worker struct {
    taskQueue chan Task
    stopped   chan struct{}
}

// run runs the worker goroutine
func (w *worker) run() {
    for {
        select {
        case task, ok := <-w.taskQueue:
            if !ok {
                return
            }
            task.started = true
            fmt.Printf("Task %d started\n", task.id)
            task.fn()
            fmt.Printf("Task %d completed\n", task.id)
            task.done = true
        case <-w.stopped:
            return
        }
    }
}

func main() {
    iterations := 3
    for i := 1; i <= iterations; i++ {
        fmt.Printf("Starting iteration %d\n", i)
        scheduler := NewScheduler(5)
        var wg sync.WaitGroup
        for j := 1; j <= 20; j++ {
            task := Task{
