                fn: func() {
                    duration := time.Duration(rand.Intn(400)+100) * time.Millisecond
                    time.Sleep(duration)
                },
            }
            wg.Add(1)
            go func(task Task) {
                scheduler.SubmitTask(task)
                wg.Done()
            }(task)
        }
        go func() {
            wg.Wait()
            scheduler.Stop()
        }()
        for {
            if scheduler.RunningTasks() == 0 {
                break
            }
            time.Sleep(10 * time.Millisecond)
        }
        fmt.Printf("Iteration %d complete\n", i)
    }
    fmt.Println("All iterations complete")
