package main

import (
    "errors"
    "fmt"
    "sync"
    "time"
)

// ErrConnectionClosed represents an error when trying to use a closed connection.
var ErrConnectionClosed = errors.New("connection closed")

// KVClient represents a simplified key-value store client.
type KVClient struct {
    mu       sync.RWMutex
    closed   bool
    cond     *sync.Cond
    conn     string // placeholder for connection state
}

// NewKVClient returns a new instance of KVClient.
func NewKVClient() *KVClient {
    return &KVClient{
        cond: sync.NewCond(&sync.RWMutex{}),
    }
}

// Get retrieves a value for a given key from a remote store.
func (c *KVClient) Get(key string) (string, error) {
    c.mu.RLock()
    if c.closed {
        c.mu.RUnlock()
        return "", ErrConnectionClosed
    }
    c.mu.RUnlock()

    // Simulate a remote call
    time.Sleep(100 * time.Millisecond)

    // Return mock data for simplicity
    return "", nil
}

// Close closes the client and cleans up resources.
func (c *KVClient) Close() error {
    c.mu.Lock()
    if c.closed {
        c.mu.Unlock()
        return nil
    }
    c.closed = true
    c.mu.Unlock()

    // Notify all waiting Get operations
    c.cond.Broadcast()

    return nil
}

func main() {
    fmt.Println("Starting test...")

    client := NewKVClient()

    var getDone, closeDone chan struct{}
    getDone = make(chan struct{})
    closeDone = make(chan struct{})

    go func() {
        _, err := client.Get("test_key")
        fmt.Printf("Get operation completed with error: %v\n", err)
        close(getDone)
    }()

    time.Sleep(10 * time.Millisecond) // small delay before closing

    err := client.Close()
    fmt.Println("Close operation completed")
    close(closeDone)

    select {
    case <-getDone:
    case <-time.After(3 * time.Second):
        fmt.Println("FAILURE")
        return
    }

    fmt.Println("Test completed in time")
    fmt.Println("SUCCESS")
}

