package main

import (
    "fmt"
    "sync"
)

// Stream represents a bidirectional communication channel.
type Stream struct {
    send chan string
    recv chan string
}

// NewStream creates a new bidirectional stream.
func NewStream() *Stream {
    return &Stream{
        send: make(chan string),
        recv: make(chan string),
    }
}

// Send sends a message through the stream.
func (s *Stream) Send(msg string) {
    s.send <- msg
}

// Receive receives a message from the stream.
func (s *Stream) Receive() string {
    return <-s.recv
}

// Close closes the stream.
func (s *Stream) Close() {
    close(s.send)
    close(s.recv)
}

// Server represents a server that communicates with a client through a stream.
type Server struct {
    stream *Stream
}

// NewServer creates a new server.
func NewServer(stream *Stream) *Server {
    return &Server{stream: stream}
}

// Run starts the server and processes incoming messages.
func (s *Server) Run() {
    for msg := range s.stream.send {
        response := fmt.Sprintf("echo: %s", msg)
        s.stream.recv <- response
    }
}

// Client represents a client that communicates with a server through a stream.
type Client struct {
    stream *Stream
    wg     sync.WaitGroup
}

// NewClient creates a new client.
func NewClient(stream *Stream) *Client {
    return &Client{stream: stream}
}

// Run starts the client, sends messages, and receives responses.
func (c *Client) Run(numMsgs int) {
    c.wg.Add(2)
    go func() {
        defer c.wg.Done()
        for i := 1; i <= numMsgs; i++ {
            msg := fmt.Sprintf("message-%d", i)
            fmt.Printf("Client sending: %s\n", msg)
            c.stream.Send(msg)
        }
        c.stream.Close()
    }()

    go func() {
        defer c.wg.Done()
        for msg := range c.stream.recv {
            fmt.Printf("Client received: %s\n", msg)
        }
    }()
    c.wg.Wait()
}

func main() {
    stream := NewStream()

    server := NewServer(stream)
    go server.Run()

    client := NewClient(stream)
    client.Run(10)

    fmt.Println("All messages have been successfully exchanged.")
}
