package main

import (
    "fmt"
    "math/rand"
    "sync"
    "time"
)

// Device represents a device with an ID and a deletion flag
type Device struct {
    ID    string
    Deleted bool
}

// DeviceManager manages a collection of devices with concurrent access
type DeviceManager struct {
    mu       sync.RWMutex
    devices  map[string]Device
    toDelete []string
}

// NewDeviceManager returns a new DeviceManager instance
func NewDeviceManager() *DeviceManager {
    return &DeviceManager{
        devices: make(map[string]Device),
    }
}

// AddDevice adds a new device to the manager
func (dm *DeviceManager) AddDevice(id string) {
    dm.mu.Lock()
    defer dm.mu.Unlock()
    dm.devices[id] = Device{ID: id}
}

// MarkDeviceForDeletion marks a device for deletion
func (dm *DeviceManager) MarkDeviceForDeletion(id string) {
    dm.mu.Lock()
    defer dm.mu.Unlock()
    if device, ok := dm.devices[id]; ok {
        device.Deleted = true
        dm.devices[id] = device
        dm.toDelete = append(dm.toDelete, id)
    }
}

// CleanupDeletedDevices cleans up devices marked for deletion
func (dm *DeviceManager) CleanupDeletedDevices() {
    dm.mu.Lock()
    toDelete := dm.toDelete
    dm.toDelete = []string{}
    dm.mu.Unlock()

    if len(toDelete) == 0 {
        return
    }

    dm.mu.Lock()
    defer dm.mu.Unlock()
    for _, id := range toDelete {
        delete(dm.devices, id)
    }
}

// GetDeviceStatus returns the status of a device
func (dm *DeviceManager) GetDeviceStatus(id string) (Device, bool) {
    dm.mu.RLock()
    defer dm.mu.RUnlock()
    device, ok := dm.devices[id]
    return device, ok
}

func main() {
    dm := NewDeviceManager()
    var wg sync.WaitGroup

    // Spawn goroutines to perform various operations
    for i := 0; i < 5; i++ {
        wg.Add(1)
        go func() {
            defer wg.Done()
            for j := 0; j < 10; j++ {
                id := fmt.Sprintf("device-%d", rand.Intn(100))
                dm.AddDevice(id)
                time.Sleep(10 * time.Millisecond)
                dm.MarkDeviceForDeletion(id)
                time.Sleep(10 * time.Millisecond)
                device, ok := dm.GetDeviceStatus(id)
                if ok {
                    fmt.Printf("Device %s status: %v\n", id, device)
                }
            }
        }()
    }

    // Periodically call CleanupDeletedDevices
    go func() {
        for {
            dm.CleanupDeletedDevices()
            time.Sleep(50 * time.Millisecond)
        }
    }()

    // Run for 5 seconds
    time.Sleep(5 * time.Second)
    wg.Wait()
}

