	mu                sync.Mutex
	cachedFailureDomain string
	fetched            bool
}

func (zs *ZoneService) GetZone() (ZoneInfo, error) {
	if zs.fetched {
		return ZoneInfo{FailureDomain: zs.cachedFailureDomain, Region: "us-west"}, nil
	}

	zs.mu.Lock()
	defer zs.mu.Unlock()

	if zs.fetched {
