package main

import (
	"errors"
	"fmt"
	"sync"
	"time"
)

var ErrConnectionClosed = errors.New("connection closed")

type KVClient struct {
	mu      sync.Mutex
	closed  bool
}

func NewKVClient() *KVClient {
	return &KVClient{}
}

func (c *KVClient) Get(key string) (string, error) {
	c.mu.Lock()
	if c.closed {
		c.mu.Unlock()
		return "", ErrConnectionClosed
	}
	c.mu.Unlock()

	// Simulate network delay
	time.Sleep(100 * time.Millisecond)

	// Return mock data
	return "mockValue", nil
}

func (c *KVClient) Close() error {
	c.mu.Lock()
	defer c.mu.Unlock()
	if c.closed {
		return nil
	}
	c.closed = true
	// Simulate cleanup
	time.Sleep(50 * time.Millisecond)
	return nil
}

func main() {
	client := NewKVClient()
	done := make(chan struct{})

	go func() {
		defer close(done)
		value, err := client.Get("someKey")
		if err != nil {
			fmt.Println("Get operation completed with error:", err)
			return
		}
		fmt.Println("Get operation completed with value:", value)
	}()

	time.Sleep(10 * time.Millisecond) // Wait for goroutine to start
	err := client.Close()
	if err != nil {
		fmt.Println("Close operation completed with error:", err)
	} else {
		fmt.Println("Close operation completed")
	}

	select {
	case <-done:
		// Test completed successfully
		fmt.Println("Test completed in time")
		fmt.Println("SUCCESS")
	case <-time.After(3 * time.Second):
		fmt.Println("Test timed out or deadlocked")
		fmt.Println("FAILURE")
	}
}
