package main

import (
	"fmt"
	"sync"
	"time"
)

type Stream struct {
	messages chan string
	wg       sync.WaitGroup
}

func NewStream() *Stream {
	return &Stream{
		messages: make(chan string),
	}
}

func (s *Stream) Send(message string) {
	s.messages <- message
}

func (s *Stream) Receive() {
	for msg := range s.messages {
		response := fmt.Sprintf("echo: [%s]", msg)
		fmt.Println(response)
	}
	s.wg.Done()
}

func Server(stream *Stream) {
	for i := 1; i <= 10; i++ {
		message := fmt.Sprintf("message-%d", i)
		stream.Send(message)
		time.Sleep(100 * time.Millisecond) // Simulate processing delay
	}
	close(stream.messages)
}

func Client(stream *Stream) {
	stream.wg.Add(1)
	go stream.Receive()

	Server(stream)

	stream.wg.Wait()
}

func main() {
	stream := NewStream()
	Client(stream)
	fmt.Println("All messages have been successfully exchanged.")
}
