package main

import (
	"fmt"
	"math/rand"
	"sync"
	"time"
)

type LoadBalancer interface {
	Start()
	Next() string
	Close()
}

type RoundRobinBalancer struct {
	servers     []string
	nextIndex   int
	mutex       sync.Mutex
	updateChan  chan []string
	shutdownChan chan struct{}
}

func NewRoundRobinBalancer(initialServers []string) *RoundRobinBalancer {
	return &RoundRobinBalancer{
		servers:     initialServers,
		nextIndex:   0,
		updateChan:  make(chan []string),
		shutdownChan: make(chan struct{}),
	}
}

func (b *RoundRobinBalancer) Start() {
	go b.watchAddresses()
}

func (b *RoundRobinBalancer) watchAddresses() {
	for {
		select {
		case newServers := <-b.updateChan:
			fmt.Println("Updating server list:", newServers)
			b.mutex.Lock()
			b.servers = newServers
			b.nextIndex = 0
			b.mutex.Unlock()
		case <-b.shutdownChan:
			return
		}
	}
}

func (b *RoundRobinBalancer) Next() string {
	b.mutex.Lock()
	defer b.mutex.Unlock()

	if len(b.servers) == 0 {
		return ""
	}
	server := b.servers[b.nextIndex]
	b.nextIndex = (b.nextIndex + 1) % len(b.servers)
	return server
}

func (b *RoundRobinBalancer) Close() {
	close(b.shutdownChan)
}

func simulateAddressUpdates(b *RoundRobinBalancer) {
	availableServers := [][]string{
		{"server1:8080", "server2:8080"},
		{"server3:8080", "server4:8080"},
		{"server5:8080", "server6:8080"},
	}

	for _, servers := range availableServers {
		time.Sleep(3 * time.Second)
		b.updateChan <- servers
	}
}

func simulateRequests(b *RoundRobinBalancer, wg *sync.WaitGroup) {
	defer wg.Done()
	for i := 0; i < 10; i++ {
		server := b.Next()
		fmt.Printf("Request %d served by %s\n", i+1, server)
		time.Sleep(time.Duration(rand.Intn(100)) * time.Millisecond)
	}
}

func main() {
	rand.Seed(time.Now().UnixNano())
	initialServers := []string{"server1:8080", "server2:8080"}
	balancer := NewRoundRobinBalancer(initialServers)
	balancer.Start()

	var wg sync.WaitGroup
	for i := 0; i < 10; i++ {
		wg.Add(1)
		go simulateRequests(balancer, &wg)
	}

	go simulateAddressUpdates(balancer)

	wg.Wait()
	balancer.Close()
	fmt.Println("Load balancer shutdown gracefully.")
}

