package main

import (
	"fmt"
	"sync"
	"time"
)

type Device struct {
	ID          string
	ParentID    string
	MountPath   string
	MountCount  int
	IsMounted   bool
	mu          sync.Mutex
}

type DeviceManager struct {
	devices map[string]*Device
	mu      sync.Mutex
}

func NewDeviceManager() *DeviceManager {
	return &DeviceManager{devices: make(map[string]*Device)}
}

func (dm *DeviceManager) AddDevice(deviceID, parentID string) {
	dm.mu.Lock()
	defer dm.mu.Unlock()
	dm.devices[deviceID] = &Device{ID: deviceID, ParentID: parentID, MountCount: 0, IsMounted: false}
	fmt.Printf("[%s] Goroutine %d: AddDevice %s with parent %s - Success\n", time.Now().Format("15:04:05"), getGoroutineID(), deviceID, parentID)
}

func (dm *DeviceManager) DeleteDevice(deviceID string) {
	dm.mu.Lock()
	device, exists := dm.devices[deviceID]
	if exists && !device.IsMounted {
		delete(dm.devices, deviceID)
		fmt.Printf("[%s] Goroutine %d: DeleteDevice %s - Success\n", time.Now().Format("15:04:05"), getGoroutineID(), deviceID)
	} else {
		fmt.Printf("[%s] Goroutine %d: DeleteDevice %s - Failed (must be unmounted)\n", time.Now().Format("15:04:05"), getGoroutineID(), deviceID)
	}
	dm.mu.Unlock()
}

func (dm *DeviceManager) MountDevice(deviceID, mountPath string) {
	dm.mu.Lock()
	device, exists := dm.devices[deviceID]
	if exists && !device.IsMounted {
		device.IsMounted = true
		device.MountPath = mountPath
		device.MountCount++
		fmt.Printf("[%s] Goroutine %d: MountDevice %s at %s - Success\n", time.Now().Format("15:04:05"), getGoroutineID(), deviceID, mountPath)
		dm.mu.Unlock()
		time.Sleep(2 * time.Second)
		dm.UnmountDevice(deviceID)
	} else {
		fmt.Printf("[%s] Goroutine %d: MountDevice %s at %s - Failed (already mounted or does not exist)\n", time.Now().Format("15:04:05"), getGoroutineID(), deviceID, mountPath)
		dm.mu.Unlock()
	}
}

func (dm *DeviceManager) UnmountDevice(deviceID string) {
	dm.mu.Lock()
	defer dm.mu.Unlock()
	device, exists := dm.devices[deviceID]
	if exists && device.IsMounted {
		device.IsMounted = false
		fmt.Printf("[%s] Goroutine %d: UnmountDevice %s - Success\n", time.Now().Format("15:04:05"), getGoroutineID(), deviceID)
	} else {
		fmt.Printf("[%s] Goroutine %d: UnmountDevice %s - Failed (not mounted)\n", time.Now().Format("15:04:05"), getGoroutineID(), deviceID)
	}
}

func (dm *DeviceManager) GetDeviceStatus(deviceID string) {
	dm.mu.Lock()
	defer dm.mu.Unlock()
	device, exists := dm.devices[deviceID]
	if exists {
		status := "unmounted"
		if device.IsMounted {
			status = "mounted"
		}
		fmt.Printf("[%s] Goroutine %d: GetDeviceStatus %s - Success (%s, count: %d)\n", time.Now().Format("15:04:05"), getGoroutineID(), deviceID, status, device.MountCount)
	} else {
		fmt.Printf("[%s] Goroutine %d: GetDeviceStatus %s - Failed (does not exist)\n", time.Now().Format("15:04:05"), getGoroutineID(), deviceID)
	}
}

func getGoroutineID() int {
	return int(1) // Just a placeholder to simulate goroutine ID.
}

func main() {
	dm := NewDeviceManager()
	dm.AddDevice("dev1", "base")

	var wg sync.WaitGroup
	wg.Add(3)

	go func() {
		defer wg.Done()
		dm.MountDevice("dev1", "/mnt/dev1")
	}()

	time.Sleep(500 * time.Millisecond)

	go func() {
		defer wg.Done()
		dm.GetDeviceStatus("dev1")
	}()

	go func() {
		defer wg.Done()
		dm.MountDevice("dev1", "/mnt/dev1")
	}()

	wg.Wait()
	dm.DeleteDevice("dev1")
}

