		case <-ctx.Done():
			resultChan <- fmt.Sprintf("%s: download canceled!", layer.ID)
			return
		default:
			time.Sleep(time.Duration(chunkSize) * time.Millisecond)
			fmt.Printf("Downloaded chunk %d for %s\n", i+1, layer.ID)
