package main

import (
	"fmt"
	"math/rand"
	"sync"
	"time"
)

type Plugin struct {
	Name string
}

func (p *Plugin) Initialize() error {
	duration := time.Duration(rand.Intn(400)+100) * time.Millisecond
	time.Sleep(duration)
	fmt.Printf("Plugin %s initialized\n", p.Name)
	return nil
}

type PluginManager struct {
	plugins []Plugin
}

func (pm *PluginManager) AddPlugin(plugin Plugin) {
	pm.plugins = append(pm.plugins, plugin)
}

func (pm *PluginManager) InitializeAll() error {
	var wg sync.WaitGroup
	errChan := make(chan error, len(pm.plugins))

	for _, plugin := range pm.plugins {
		wg.Add(1)
		go func(p Plugin) {
			defer wg.Done()
			if err := p.Initialize(); err != nil {
				errChan <- err
			}
		}(plugin)
	}

	wg.Wait()
	close(errChan)

	if err := <-errChan; err != nil {
		return err
	}

	fmt.Println("All plugins initialized successfully")
	return nil
}

func main() {
	rand.Seed(time.Now().UnixNano())

	startTime := time.Now()

	pm := PluginManager{}
	pm.AddPlugin(Plugin{Name: "plugin-1"})
