package main

import (
	"bytes"
	"fmt"
	"io"
	"log"
	"os/exec"
	"sync"
)

// StreamCommand executes the command and returns an io.Reader for its stdout.
// It captures stderr and includes it in the error if the command fails.
func StreamCommand(cmd *exec.Cmd) (io.Reader, error) {
	var stderrBuffer bytes.Buffer
	cmd.Stdout = &stderrBuffer // We will read this for stdout
	cmd.Stderr = &stderrBuffer // We will read this for stderr

	// Start command execution.
	if err := cmd.Start(); err != nil {
		return nil, err
	}

	// Create a wait group to wait for the command to finish.
	var wg sync.WaitGroup
	wg.Add(1)

	// Go routine to handle the command's stdout.
	go func() {
		defer wg.Done()
		if err := cmd.Wait(); err != nil {
			if exitError, ok := err.(*exec.ExitError); ok {
				// We need to include stderr if the command failed.
				if stderr := stderrBuffer.Bytes(); len(stderr) > 0 {
					log.Printf("Command failed with error: %v, stderr: %s", exitError, stderr)
				} else {
					log.Printf("Command failed with error: %v", exitError)
				}
			}
		}
	}()

	// It returns a pipe reader to read stdout.
	stdoutReader, stdoutWriter := io.Pipe()

	// Copy stdout to the pipe writer.
	go func() {
		defer stdoutWriter.Close()
		if _, err := io.Copy(stdoutWriter, &stderrBuffer); err != nil {
			log.Printf("Failed to copy stdout to pipe writer: %v", err)
		}
	}()

	return stdoutReader, nil
}

func main() {
	cmd := exec.Command("echo", "hello world")
	reader, err := StreamCommand(cmd)
	if err != nil {
		log.Fatal(err)
	}

	output, err := io.ReadAll(reader)
	if err != nil {
		log.Printf("Command failed: %v", err)
	}
	fmt.Println(string(output))

	// Running a failed command example
	failedCmd := exec.Command("sh", "-c", "echo error message >&2; exit 1")
	failedReader, err := StreamCommand(failedCmd)
	if err != nil {
		log.Fatal(err)
	}

	failedOutput, err := io.ReadAll(failedReader)
	if err != nil {
		log.Printf("Command failed: %v", err)
	}
	fmt.Println(string(failedOutput))

	// Running a command with large stderr example
	largeErrCmd := exec.Command("sh", "-c", "dd if=/dev/zero bs=1k count=1000 of=/dev/stderr 2>/dev/null; echo success")
	largeErrReader, err := StreamCommand(largeErrCmd)
	if err != nil {
		log.Fatal(err)
	}

	largeErrOutput, err := io.ReadAll(largeErrReader)
	if err != nil {
		log.Printf("Command failed: %v", err)
	}
	fmt.Println(string(largeErrOutput))

	// Running a command with mixed output example
	mixedCmd := exec.Command("sh", "-c", "echo mixed output; echo error message >&2; exit 1")
	mixedReader, err := StreamCommand(mixedCmd)
	if err != nil {
		log.Fatal(err)
	}

	mixedOutput, err := io.ReadAll(mixedReader)
	if err != nil {
		log.Printf("Command failed: %v", err)
	}
	fmt.Println(string(mixedOutput))
}

