package main

import (
	"fmt"
	"math/rand"
	"sync"
	"time"
)

type Task struct {
	Name     string
	Status   string
	Metadata map[string]interface{}
}

type TaskManager struct {
	sync.Mutex
	tasks  map[string]*Task
	status chan *Task
}

func NewTaskManager(bufferSize int) *TaskManager {
	return &TaskManager{
		tasks:  make(map[string]*Task),
		status: make(chan *Task, bufferSize),
	}
}

func (tm *TaskManager) SetTaskStatus(taskName string, status string) {
	tm.Lock()
	defer tm.Unlock()

	if task, exists := tm.tasks[taskName]; exists && task.Status == status {
		return
	}

	if task, exists := tm.tasks[taskName]; exists {
		task.Status = status
	} else {
		tm.tasks[taskName] = &Task{Name: taskName, Status: status, Metadata: make(map[string]interface{})}
	}
	tm.status <- tm.tasks[taskName]
}

func (tm *TaskManager) SyncBatch() {
	for task := range tm.status {
		if rand.Intn(100) < 20 {
			fmt.Printf("Sync failed for task: %s\n", task.Name)
			tm.DeleteTaskStatus(task.Name)
		} else {
			fmt.Printf("Successfully synced task: %s with status: %s\n", task.Name, task.Status)
		}
	}
}

func (tm *TaskManager) DeleteTaskStatus(taskName string) {
	tm.Lock()
	defer tm.Unlock()
	delete(tm.tasks, taskName)
}

func worker(tm *TaskManager, workerID int, wg *sync.WaitGroup) {
	defer wg.Done()
	for i := 0; i < 5; i++ {
		taskName := fmt.Sprintf("Task-%d", rand.Intn(10))
		status := []string{"pending", "running", "completed"}[rand.Intn(3)]
		tm.SetTaskStatus(taskName, status)
		time.Sleep(time.Duration(rand.Intn(1000)) * time.Millisecond)
	}
}

func main() {
	rand.Seed(time.Now().UnixNano())
	taskManager := NewTaskManager(5)

	var wg sync.WaitGroup
	for i := 0; i < 10; i++ {
		wg.Add(1)
		go worker(taskManager, i, &wg)
	}

	go taskManager.SyncBatch()

	wg.Wait()
	time.Sleep(2 * time.Second) // Allow some time for final syncs to happen

	close(taskManager.status)
	time.Sleep(1 * time.Second) // Wait for the sync batch to finish up
}

