package main

import (
	"fmt"
	"math/rand"
	"os"
	"os/signal"
	"sync"
	"time"
)

type ResourceWatcher struct {
	mu sync.Mutex
}

func (rw *ResourceWatcher) WatchAndSync(stopCh <-chan struct{}) error {
	rw.mu.Lock()
	defer rw.mu.Unlock()

	ticker := time.NewTicker(1 * time.Second)
	defer ticker.Stop()

	for {
		select {
		case <-stopCh:
			fmt.Println("Shutdown signal received")
			return nil
		case <-ticker.C:
			if rand.Float32() < 0.2 { // 20% chance of error
				return fmt.Errorf("watch error occurred: simulated failure")
			}
			update := fmt.Sprintf("Received resource update: item-%d", rand.Intn(100))
			fmt.Println(update)

			go rw.Resync() // Resync concurrently
		}
	}
}

func (rw *ResourceWatcher) Resync() {
	fmt.Println("Performing resync...")
	time.Sleep(2 * time.Second) // Simulate time taken to resync
}

func main() {
	rand.Seed(time.Now().UnixNano())
	stopCh := make(chan struct{})
	defer close(stopCh)

	// Handle unexpected shutdowns gracefully
	signalCh := make(chan os.Signal, 1)
	signal.Notify(signalCh, os.Interrupt)

	rw := &ResourceWatcher{}
	for {
		err := rw.WatchAndSync(stopCh)
		if err != nil {
			fmt.Println(err)
			fmt.Println("Restarting watch cycle...")
			time.Sleep(1 * time.Second) // Brief pause before retry
		}
		select {
		case <-signalCh:
			close(stopCh)
			return
		default:
			// Continue on error or by completing the WatchAndSync method
		}
	}
}

